const a={id:1,title:"Carbonara de cogumelos"},e=[{name:"esparguete",quantity:"350",quantity_type:"g"},{name:"azeite",quantity:"1",quantity_type:"q.b"},{name:"chalotas banana",quantity:"2",quantity_type:"unidade"},{name:"manteiga",quantity:"25",quantity_type:"g"},{name:"cogumelos",quantity:"500",quantity_type:"g"},{name:"alho",quantity:"2",quantity_type:"unidade"},{name:"tomilho",quantity:"2",quantity_type:"unidade"},{name:"molho de soja",quantity:"60",quantity_type:"ml"},{name:"ovos",quantity:"3",quantity_type:"unidade"},{name:"parmes\xE3o",quantity:"100",quantity_type:"g"},{name:"salsa",quantity:"1",quantity_type:"q.b."}],t=[{order:0,text:"Cozer a massa"},{order:1,text:"Enquanto a massa coze, aquecer o azeite e guisar as chalotas com uma pitada de sal "},{order:2,text:"Adicionar a manteiga com a mistura de cogumelos e alourar a temperatura alta at\xE9 ficarem dourados, reservar cogumelos para decorar"},{order:3,text:"Juntar o alho, o tomilho e as especiarias e deixar cozinhar durante mais um minuto "},{order:4,text:"Adicionar o molho de soja e continuar a cozinhar at\xE9 o alho estar alourado"},{order:5,text:"Numa ta\xE7a pequena, misturar os ovos com parmes\xE3o, reservar um pouco para decorar, e temperar com sal e pimenta"},{order:6,text:"Reservar uma parte da \xE1gua de cozer a massa e escorrer a massa"},{order:7,text:"Juntar e misturar depressa juntamente com a mistura de ovos e um pouco da \xE1gua da cozedura"},{order:8,text:"Misturar a massa at\xE9 que esteja envolta uniformemente pelo molho e o molho n\xE3o comece a solidificar"},{order:9,text:"Adicionar eventualmente um pouco mais de \xE1gua da massa para que a mistura fique bem cremosa"},{order:10,text:"Envolver e distribuir por 4 pratos"},{order:11,text:"Polvilhar com os cogumelos reservados e o queijo e servir"}],o={details:a,ingredients:e,instructions:t};export{o as default,a as details,e as ingredients,t as instructions};
